mapper.select:
SELECT provider.id, provider.`name`, provider.phone, certification.id_card_no as id_number, setting.city_name, setting.can_pick_up, provider.`status`, certification.certification_time FROM serve_provider AS provider LEFT JOIN serve_provider_settings AS setting ON setting.id = provider.id LEFT JOIN worker_certification AS certification ON certification.id = provider.id
provider.type = 2 AND certification.certification_status = 2
AND provider.`name` LIKE concat('%',#{name},'%')
AND provider.phone = #{phone}
AND provider.status = #{status}
AND setting.can_pick_up = #{canPickUp}
ORDER BY certification.certification_time desc

SELECT provider.id, provider.`name`, provider.phone, certification.id_number, setting.city_name, setting.can_pick_up, provider.`status`, certification.certification_time FROM serve_provider AS provider LEFT JOIN serve_provider_settings AS setting ON setting.id = provider.id LEFT JOIN agency_certification AS certification ON certification.id = provider.id
provider.type = 3 AND certification.certification_status = 2
AND provider.`name` LIKE concat('%',#{name},'%')
AND provider.phone = #{phone}
AND provider.status = #{status}
AND setting.can_pick_up = #{canPickUp}
ORDER BY certification.certification_time desc
